	    		function create_keke_witkey_currencies(){		 		 $data =  array(); 					if(!is_null($this->_currencies_id)){ 				 $data['currencies_id']=$this->_currencies_id;			}			if(!is_null($this->_title)){ 				 $data['title']=$this->_title;			}			if(!is_null($this->_code)){ 				 $data['code']=$this->_code;			}			if(!is_null($this->_symbol_left)){ 				 $data['symbol_left']=$this->_symbol_left;			}			if(!is_null($this->_symbol_right)){ 				 $data['symbol_right']=$this->_symbol_right;			}			if(!is_null($this->_decimal_point)){ 				 $data['decimal_point']=$this->_decimal_point;			}			if(!is_null($this->_thousands_point)){ 				 $data['thousands_point']=$this->_thousands_point;			}			if(!is_null($this->_decimal_places)){ 				 $data['decimal_places']=$this->_decimal_places;			}			if(!is_null($this->_value)){ 				 $data['value']=$this->_value;			}			if(!is_null($this->_last_updated)){ 				 $data['last_updated']=$this->_last_updated;			}			 return $this->_currencies_id = $this->_db->inserttable($this->_tablename,$data,1,$this->_replace); 		 } 
	    		function edit_keke_witkey_currencies(){ 		 		 $data =  array();  					if(!is_null($this->_currencies_id)){ 				 $data['currencies_id']=$this->_currencies_id;			}			if(!is_null($this->_title)){ 				 $data['title']=$this->_title;			}			if(!is_null($this->_code)){ 				 $data['code']=$this->_code;			}			if(!is_null($this->_symbol_left)){ 				 $data['symbol_left']=$this->_symbol_left;			}			if(!is_null($this->_symbol_right)){ 				 $data['symbol_right']=$this->_symbol_right;			}			if(!is_null($this->_decimal_point)){ 				 $data['decimal_point']=$this->_decimal_point;			}			if(!is_null($this->_thousands_point)){ 				 $data['thousands_point']=$this->_thousands_point;			}			if(!is_null($this->_decimal_places)){ 				 $data['decimal_places']=$this->_decimal_places;			}			if(!is_null($this->_value)){ 				 $data['value']=$this->_value;			}			if(!is_null($this->_last_updated)){ 				 $data['last_updated']=$this->_last_updated;			}			if($this->_where){ 				 return $this->_db->updatetable($this->_tablename,$data,$this->getWhere()); 			 } 			 else{ 				 $where = array('currencies_id' => $this->_currencies_id); 				 return $this->_db->updatetable($this->_tablename,$data,$where); 			} 		 } 
	    		function query_keke_witkey_currencies($is_cache=0, $cache_time=0){ 			 if($this->_where){ 				 $sql = "select * from $this->_tablename where ".$this->_where; 			 } 			 else{ 				 $sql = "select * from $this->_tablename"; 			 } 			if ($is_cache) {			 $this->_cache_config ['is_cache'] = $is_cache;			}			if ($cache_time) {			 $this->_cache_config ['time'] = $cache_time;			 }			 if ($this->_cache_config ['is_cache']) {			     if (CACHE_TYPE) {					 $keke_cache = new keke_cache_class ( CACHE_TYPE );					 $id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');						$data = $keke_cache->get ( $id );							if ($data) {								return $data; 							} else { 								$res = $this->_dbop->query ( $sql ); 								$keke_cache->set ( $id, $res,$this->_cache_config['time'] ); 					 			$this->_where = ""; 								return $res; 							} 						} 			 }else{			 	$this->_where = ""; 				return  $this->_dbop->query ( $sql ); 			 }		 } 
	    		function count_keke_witkey_currencies(){ 			 if($this->_where){ 				 $sql = "select count(*) as count from $this->_tablename where ".$this->_where; 			 } 			 else{ 				 $sql = "select count(*) as count from $this->_tablename"; 			 } 			 $this->_where = ""; 			 return $this->_dbop->getCount($sql); 		 } 
	    		function del_keke_witkey_currencies(){ 			 if($this->_where){ 				 $sql = "delete from $this->_tablename where ".$this->_where; 			 } 			 else{ 				 $sql = "delete from $this->_tablename where currencies_id = $this->_currencies_id "; 			 } 			 $this->_where = ""; 			 return $this->_dbop->execute($sql); 		 } 
   }
